mapper.select:
		select use_wechat.`id`,`uid`,t2.username,use_wechat.`Wechat`,use_wechat.`Password`,`Data62`,`LastDate`,
		`TotalTaskQuantity`,`TodayTaskQuantity`,use_wechat.`Stauts`,`Remark`,`Createdate`,`Modifydate`,`Taskid`,
		`randomid`,`sessionkey`,`deviceid`,`maxsynckey`,`uin`,`AutoAuthKey`,`Cookies`,`CurrentsyncKey`,
		`DeviceName`,`DeviceType`,`NickName`,use_wechat.`UserName` user,`UserExt`
		from use_wechat left join sys_user t2 on use_wechat.uid=t2.user_id

		  		   and use_wechat.id = #{id} 
		  		   and uid = #{uid} 
		  		   and use_wechat.Wechat = #{wechat} 
		  		   and use_wechat.Password = #{password} 
		  		   and use_wechat.Wechat != #{wechatNo} 
		  		   and Data62 = #{data62} 
		  		   and LastDate = #{lastdate} 
		  		   and TotalTaskQuantity = #{totaltaskquantity} 
		  		   and TodayTaskQuantity = #{todaytaskquantity} 
		  		   and use_wechat.Stauts = #{stauts} 
		  		   and Remark = #{remark} 
		  		   and Createdate = #{createdate} 
		  		   and createdate <= DATE_FORMAT(date_sub(curdate(),interval 1 day),'%Y-%m-%d %h:%i:%s') 
		  		   and Modifydate = #{modifydate} 
		  		   and Taskid = #{taskid} 
		  		   and randomid = #{randomid} 
		  		   and sessionkey = #{sessionkey} 
		  		   and deviceid = #{deviceid} 
		  		   and maxsynckey = #{maxsynckey} 
		  		   and uin = #{uin} 
		  		   and AutoAuthKey = #{autoauthkey} 
		  		   and Cookies = #{cookies} 
		  		   and CurrentsyncKey = #{currentsynckey} 
		  		   and DeviceName = #{devicename} 
		  		   and DeviceType = #{devicetype} 
		  		   and NickName = #{nickname} 
		  		   and use_wechat.UserName = #{username} 
		  		   and UserExt = #{userext} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select `id`,`uid`,`Wechat`,`Password`,`Data62`,`LastDate`,`TotalTaskQuantity`,`TodayTaskQuantity`,`Stauts`,`Remark`,`Createdate`,`Modifydate`,`Taskid`,`randomid`,`sessionkey`,`deviceid`,`maxsynckey`,`uin`,`AutoAuthKey`,`Cookies`,`CurrentsyncKey`,`DeviceName`,`DeviceType`,`NickName`,`UserName`,`UserExt` from use_wechat

	 and id = #{id} 
	 and uid = #{uid} 
	 and Wechat = #{wechat} 
	 and Password = #{password} 
	 and Data62 = #{data62} 
	 and LastDate = #{lastdate} 
	 and TotalTaskQuantity = #{totaltaskquantity} 
	 and TodayTaskQuantity = #{todaytaskquantity} 
	 and Stauts = #{stauts} 
	 and Remark = #{remark} 
	 and Createdate = #{createdate} 
	 and modifydate <= DATE_FORMAT(date_sub(curdate(),interval 1 day),'%Y-%m-%d %h:%i:%s') 
	 and Modifydate = #{modifydate} 
	 and Taskid = #{taskid} 
	 and randomid = #{randomid} 
	 and sessionkey = #{sessionkey} 
	 and deviceid = #{deviceid} 
	 and maxsynckey = #{maxsynckey} 
	 and uin = #{uin} 
	 and AutoAuthKey = #{autoauthkey} 
	 and Cookies = #{cookies} 
	 and CurrentsyncKey = #{currentsynckey} 
	 and DeviceName = #{devicename} 
	 and DeviceType = #{devicetype} 
	 and NickName = #{nickname} 
	 and UserName = #{username} 
	 and UserExt = #{userext} 

order by createdate desc
limit 1

select count(*) from use_wechat

  		   and id = #{id} 
  		   and uid = #{uid} 
  		   and Wechat = #{wechat} 
  		   and Password = #{password} 
  		   and Data62 = #{data62} 
  		   and LastDate = #{lastdate} 
  		   and TotalTaskQuantity = #{totaltaskquantity} 
  		   and TodayTaskQuantity = #{todaytaskquantity} 
  		   and Stauts = #{stauts} 
  		   and Remark = #{remark} 
  		   and Createdate = #{createdate} 
  		   and Modifydate = #{modifydate} 
  		   and Taskid = #{taskid} 
  		   and randomid = #{randomid} 
  		   and sessionkey = #{sessionkey} 
  		   and deviceid = #{deviceid} 
  		   and maxsynckey = #{maxsynckey} 
  		   and uin = #{uin} 
  		   and AutoAuthKey = #{autoauthkey} 
  		   and Cookies = #{cookies} 
  		   and CurrentsyncKey = #{currentsynckey} 
  		   and DeviceName = #{devicename} 
  		   and DeviceType = #{devicetype} 
  		   and NickName = #{nickname} 
  		   and UserName = #{username} 
  		   and UserExt = #{userext}

        select t.`id`,t.`uid`,t.Wechat,t.`Password`,t.`Data62`,t.`LastDate`,t.`TotalTaskQuantity`,t.`TodayTaskQuantity`,t.`Stauts`,t.`Remark`,t.`Createdate`
        ,t.`Modifydate`,t.`Taskid`,t.`randomid`,t.`sessionkey`,t.`deviceid`,t.`maxsynckey`,t.`uin`,t.`AutoAuthKey`,t.`Cookies`,t.`CurrentsyncKey`,t.`DeviceName`
        ,t.`DeviceType`,t.`NickName`,t.`UserName`,t.`UserExt`,sys_user.parentId parentId from use_wechat t,sys_user
        where t.uid=sys_user.user_id and  sys_user.maxnum > t.TodayTaskQuantity and t.Taskid is null

			and not EXISTS(select WxId from wx_taskdetail where wx_taskdetail.WxId = t.id
			and wx_taskdetail.Stauts=1 and wx_taskdetail.Taskid in
			#{taskInfoIds}

		   )

			 and LastDate < #{lastdate} 


				and t.Stauts  in
				#{stauts}



			 and t.Taskid = #{taskid} 

			limit #{limit}
